import React from 'react';
import carousel1 from "../images/Home1.jpg";
import carousel2 from "../images/hom2.jpg";
import carousel3 from "../images/hom3.jpg";


const Carousel = () => {
  return (
    <div
      id="carouselExampleCaptions"
      className="carousel slide relative w-full max-w-screen-lg mx-auto overflow-hidden rounded-lg shadow-lg"
      data-bs-ride="carousel"
    >
      {/* Indicators */}
      <div className="carousel-indicators absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-2">
        {[0, 1, 2].map((index) => (
          <button
            key={index}
            type="button"
            data-bs-target="#carouselExampleCaptions"
            data-bs-slide-to={index}
            className={`w-3 h-3 rounded-full transition-all ${
              index === 0 ? "bg-white" : "bg-gray-500"
            } hover:bg-gray-300`}
            aria-label={`Slide ${index + 1}`}
          ></button>
        ))}
      </div>

      {/* Carousel Inner */}
      <div className="carousel-inner relative w-full h-[400px] sm:h-[300px]"> {/* Fixed height for the carousel container */}
        <div className="carousel-item active h-full">
          <img
            src={carousel1}
            className="d-block w-full h-full object-cover" /* object-cover ensures cropping */
            alt="Slide 1"
          />
        </div>
        <div className="carousel-item h-full">
          <img
            src={carousel2}
            className="d-block w-full h-full object-cover"
            alt="Slide 2"
          />
        </div>
        <div className="carousel-item h-full">
          <img
            src={carousel3}
            className="d-block w-full h-full object-cover"
            alt="Slide 3"
          />
        </div>
      </div>

      {/* Controls */}
      <button
        className="carousel-control-prev absolute top-1/2 left-4 transform -translate-y-1/2 opacity-0 hover:opacity-100 transition-opacity duration-300"
        type="button"
        data-bs-target="#carouselExampleCaptions"
        data-bs-slide="prev"
      >
        <span className="carousel-control-prev-icon bg-black bg-opacity-50 p-3 rounded-full hover:bg-opacity-80" aria-hidden="true"></span>
        <span className="visually-hidden">Previous</span>
      </button>
      <button
        className="carousel-control-next absolute top-1/2 right-4 transform -translate-y-1/2 opacity-0 hover:opacity-100 transition-opacity duration-300"
        type="button"
        data-bs-target="#carouselExampleCaptions"
        data-bs-slide="next"
      >
        <span className="carousel-control-next-icon bg-black bg-opacity-50 p-3 rounded-full hover:bg-opacity-80" aria-hidden="true"></span>
        <span className="visually-hidden">Next</span>
      </button>
    </div>
  );
};

export default Carousel;
