import React, { useState, useEffect } from 'react';

const Carousel = () => {
  const [activeSlide, setActiveSlide] = useState(0);

  const slides = [
    {
      image: "/api/placeholder/1200/800",
      title: "Explore the World",
      subtitle: "Discover breathtaking destinations",
      description: "Begin your journey today with unforgettable experiences"
    },
    {
      image: "/api/placeholder/1200/800",
      title: "Adventure Awaits",
      subtitle: "Create lasting memories",
      description: "Find your perfect getaway with our expert guides"
    },
    {
      image: "/api/placeholder/1200/800",
      title: "Travel in Style",
      subtitle: "Luxury meets adventure",
      description: "Experience comfort and excitement in every journey"
    }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="relative w-full bg-gray-900">
      <div className="relative h-[70vh] overflow-hidden">
        {slides.map((slide, index) => (
          <div
            key={index}
            className={`absolute w-full h-full transition-all duration-700 ease-in-out transform
              ${index === activeSlide ? 'opacity-100 translate-x-0' : 'opacity-0 translate-x-full'}`}
          >
            {/* Image with overlay */}
            <div className="relative w-full h-full">
              <img
                src={slide.image}
                className="w-full h-full object-cover"
                alt={`Slide ${index + 1}`}
              />
              <div className="absolute inset-0 bg-black bg-opacity-40" />
            </div>

            {/* Text content with animations */}
            <div className="absolute inset-0 flex flex-col items-center justify-center text-white text-center px-4">
              <h2 
                className="text-4xl md:text-6xl font-bold mb-4 opacity-0"
                style={{
                  animation: index === activeSlide ? 'slideDown 0.8s ease-out forwards' : 'none',
                  textShadow: '2px 2px 4px rgba(0,0,0,0.5)'
                }}
              >
                {slide.title}
              </h2>
              <p 
                className="text-xl md:text-2xl mb-4 opacity-0"
                style={{
                  animation: index === activeSlide ? 'slideUp 0.8s ease-out 0.3s forwards' : 'none'
                }}
              >
                {slide.subtitle}
              </p>
              <p 
                className="text-lg md:text-xl max-w-2xl opacity-0"
                style={{
                  animation: index === activeSlide ? 'fadeIn 1s ease-out 0.6s forwards' : 'none'
                }}
              >
                {slide.description}
              </p>
            </div>
          </div>
        ))}

        {/* Navigation arrows */}
        <button
          onClick={() => setActiveSlide((prev) => (prev - 1 + slides.length) % slides.length)}
          className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-white bg-opacity-20 hover:bg-opacity-30 rounded-full p-3 transition-all duration-200"
        >
          <span className="text-white text-2xl">←</span>
        </button>
        <button
          onClick={() => setActiveSlide((prev) => (prev + 1) % slides.length)}
          className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-white bg-opacity-20 hover:bg-opacity-30 rounded-full p-3 transition-all duration-200"
        >
          <span className="text-white text-2xl">→</span>
        </button>

        {/* Indicators */}
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-2">
          {slides.map((_, index) => (
            <button
              key={index}
              onClick={() => setActiveSlide(index)}
              className={`w-3 h-3 rounded-full transition-all duration-300 ${
                index === activeSlide ? 'bg-white w-6' : 'bg-white/50'
              }`}
            />
          ))}
        </div>
      </div>

      <style jsx>{`
        @keyframes slideDown {
          from {
            transform: translateY(-50px);
            opacity: 0;
          }
          to {
            transform: translateY(0);
            opacity: 1;
          }
        }

        @keyframes slideUp {
          from {
            transform: translateY(50px);
            opacity: 0;
          }
          to {
            transform: translateY(0);
            opacity: 1;
          }
        }

        @keyframes fadeIn {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }
      `}</style>
    </div>
  );
};

export default Carousel;
