import { Link, useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const TourGuideHeader = () => {
  let navigate = useNavigate();

  const user = JSON.parse(sessionStorage.getItem("active-guide"));
  console.log(user);

  const guideLogout = () => {
    toast.success("logged out!!!", {
      position: "top-center",
      autoClose: 1000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
    });
    sessionStorage.removeItem("active-guide");
    sessionStorage.removeItem("guide-jwtToken");
    window.location.reload(true);
    setTimeout(() => {
      navigate("/home");
    }, 2000); // Redirect after 2 seconds
  };

  return (
    <ul className="navbar-nav ms-auto mb-2 mb-lg-0 me-5">
      <li className="nav-item dropdown">
        <a
          className="nav-link dropdown-toggle text-color"
          href="#"
          id="navbarDropdown"
          role="button"
          data-bs-toggle="dropdown"
          aria-expanded="false"
        >
          <b> My Tours</b>
        </a>
        <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
          <li>
            <Link
              to="/tour-guide/tour/add"
              className="dropdown-item text-color"
            >
              <b>Add Tour</b>
            </Link>
          </li>
          <li>
            <Link
              to="/tour-guide/tours/view"
              className="dropdown-item text-color"
            >
              <b>View Tours</b>
            </Link>
          </li>
        </ul>
      </li>

      <li className="nav-item">
        <Link
          to="/tour-guide/tour/booking/view"
          className="nav-link active"
          aria-current="page"
        >
          <b className="text-color">My Tour Bookings</b>
        </Link>
      </li>
      <li className="nav-item">
        <Link
          to=""
          className="nav-link active"
          aria-current="page"
          onClick={guideLogout}
        >
          <b className="text-color">Logout</b>
        </Link>
        <ToastContainer />
      </li>
    </ul>
  );
};

export default TourGuideHeader;
