import { Parallax } from "react-parallax";
import carousel1 from "../images/hom1.jpg";
import carousel2 from "../images/hom2.jpg";
import carousel3 from "../images/hom3.jpg";
import carousel4 from "../images/travel.mp4";

const ParallaxCarousel = () => {
  return (
    <div className="w-full">
      {/* Parallax Section 1 */}
      <Parallax bgImage={carousel1} strength={300} className="h-[500px] flex items-center justify-center">
        <h1 className="text-white text-4xl font-bold bg-black bg-opacity-50 px-6 py-2 rounded-lg">
          Welcome to Our Site
        </h1>
      </Parallax>

      {/* Parallax Section 2 */}
      <Parallax bgImage={carousel2} strength={200} className="h-[500px] flex items-center justify-center">
        <h1 className="text-white text-3xl font-semibold bg-black bg-opacity-50 px-6 py-2 rounded-lg">
          Explore Amazing Destinations
        </h1>
      </Parallax>

      {/* Parallax Section 3 */}
      <Parallax bgImage={carousel3} strength={250} className="h-[500px] flex items-center justify-center">
        <h1 className="text-white text-3xl font-semibold bg-black bg-opacity-50 px-6 py-2 rounded-lg">
          Discover New Adventures
        </h1>
      </Parallax>

      {/* Parallax Video Section */}
      <div className="relative h-[500px] w-full overflow-hidden">
        <video
          autoPlay
          loop
          muted
          className="absolute top-0 left-0 w-full h-full object-cover"
        >
          <source src={carousel4} type="video/mp4" />
        </video>
        <div className="absolute top-0 left-0 w-full h-full flex items-center justify-center bg-black bg-opacity-40">
          <h1 className="text-white text-4xl font-bold">Experience the Journey</h1>
        </div>
      </div>
    </div>
  );
};

export default ParallaxCarousel;
